import React,{useState} from 'react'

export default function Textform() {
    const [text,setText] = {}
    return (
        <div>
            <div className="mb-3">
                <label htmlFor="exampleFormControlTextarea1" className="form-label" >Example textarea</label>
                <textarea className="form-control" id="exampleFormControlTextarea1" rows="3" placeholder='Enter the text'></textarea>
                <button className="btn btn-primary my-4 mx-2">UpperCase</button>
                <button className="btn btn-primary my-4 mx-2">Lowercase</button>
                <button className="btn btn-primary my-4 mx-2">Clear</button>
            </div>
        </div>
    )
}
