import './App.css';
// import React, { Component } from 'react'
// import PropTypes from 'prop-types'
import Navbar from './Components/Navbar';
import Hero from './Components/Hero';
import Textform from './Components/Textform';
import { useState } from 'react';

function App() {
  const [mode, setMode] = useState('light');
  const [buttonText, setButtonText] = useState("Dark");
  let togglemodeDark = () => {
    if (mode === 'light') {
      setMode('secondary');
      document.body.style.backgroundColor = '#864879'
      setButtonText("Enable Light Mode");
    }
    else {
      setMode("light");
      document.body.style.backgroundColor = 'white'
      setButtonText("Enable Dark Mode");
    }
  }

  const [contrastMode, setContrastMode] = useState('light');
  let togglemodeContrast = () => {
    if (contrastMode === 'light') {
      setContrastMode('dark');
      document.body.style.backgroundColor = '#180A0A'
      document.body.style.color = 'white'
      setButtonText("Enable Light Mode");
    }
    else {
      setContrastMode('light');
      document.body.style.backgroundColor = 'white';
      setButtonText("Enable Light Mode");
    }
  }


  return (
    <>
      <Navbar title="V1nlands" mode={mode} togglemodeDark={togglemodeDark} buttonText={buttonText} togglemodeContrast={togglemodeContrast} />
      <div className='container'>
        <Hero />

        <Textform />

      </div>
    </>
  );
}

export default App;
